                      //console.log(pos);
                      if(pos == 0){
                        c = c + '<div class="col-left col-lg-6 col-md-6"><div class="item art-item-blog fsz"><article class="magplus-post"><div class="magplus-parentover"><div class="magplus-list-media"><a class="imgthub-post-blog fsz" href="'+ pu +'" style="background:url(\''+ pi +'\')"></a><div class="magplus-post-share"><div class="magplus-cell"></div></div></div><div class="magplus-box-list"><div class="magplus-box-inside"><div class="magplus-list-content"><div class="entry-header"><h3 class="entry-title"><a href="'+ pu +'" rel="bookmark">'+ pt +'</a></h3></div></div><div class="article-meta"><span class="byline"><i class="ion-ios-person-outline"></i><span class="author vcard"><a class="url fn n" href="'+ pau +'">'+ pa +'</a></span></span> <span class="posted-on"><i class="ion-ios-clock-outline"></i><a href="'+pu+'" rel="bookmark"><time class="entry-date published updated" datetime="'+pp+'">'+ pd +'</time></a></span></div><Div class="entry-content"><div>'+ snip(p.content.$t,10) + '...</div><a class="btn-read" href="'+ pu +'">Read More</a></div></div></div></div></article></div></div><div class="col-rt  col-lg-6 col-md-6">';

                      }else{


                      c = c + '<div class="item art-item-blog rsize"><article class="magplus-post"><div class="magplus-parentover"><div class="magplus-list-media"><a class="imgthub-post-blog rsize" href="'+ pu +'" style="background:url(\''+ pi +'\')"></a><div class="magplus-post-share"><div class="magplus-cell"></div></div></div><div class="magplus-box-list"><div class="magplus-box-inside"><div class="magplus-list-content"><div class="entry-header"><h3 class="entry-title"><a href="'+ pu +'" rel="bookmark">'+ pt +'</a></h3></div></div><div class="article-meta"><span class="byline"><i class="ion-ios-person-outline"></i><span class="author vcard"><a class="url fn n" href="'+ pau +'">'+ pa +'</a></span></span> <span class="posted-on"><i class="ion-ios-clock-outline"></i><a href="'+pu+'" rel="bookmark"><time class="entry-date published updated" datetime="'+pp+'">'+ pd +'</time></a></span></div></div></div></div></article></div>'
                      }



                      break;

                  }

                }

              }//else

              switch(t){
                case "list":
                  c = c + ''; 
                  break;
                case "gallery":
                  c = c + '</div></div><div class="clearfix"></div>'; 
                  break;
              }


            },
            complete : function(){

              switch(t){
                case "list":
					c = '<div id="applyme_car" class="owl-carousel owl-theme applyme_car ">' + c + '</div>';


					 $th.append(c);

				break;
				case "gallery":
					$th.append(c);
				break;
				}



				  switch(t){
                case "list":
				$th.find('.applyme_car').owlCarousel({
                  loop:true,
                  margin:0,
                  nav:false,
                  dots:true,
                  autoplayTimeout: 3000,
                  autoplayHoverPause: true,
                  autoplay:true,
                  responsive:{
                    0:{
                      items:1
                    },
                    750:{
                      items:2
                    },
                    1000:{
                      items:3
                    }
                  }
                });

				break;
				}



            }
          });


        });
        //

body.script:
