$(document).ready(function() {
  $('img').map(function() {
    var $img = $(this);
    var filename = $img.attr('src');
    $img.attr('title', filename.substring((filename.lastIndexOf('/')) + 1, filename.lastIndexOf('.')));
    $img.attr('alt', filename.substring((filename.lastIndexOf('/')) + 1, filename.lastIndexOf('.')));
  })
});

$(".Label a, .magplus-max-limit").attr("href", function($this, href) {
  return href.replace(href, href + "?&max-results=" + perPage);
});
$(document).ready(function() {
  $('.share-toggle').on('click', function () {
    var $property = 'width';
    if($(this).parent().hasClass('vertical')){
      $property = 'height';
    }
    if ($(this).parent().hasClass('expanded')) {
      $(this).siblings('.ext-share').css($property, '0');
      $(this).parent().removeClass('expanded');
    }
    else {
      var $width = 50;
      var $total = $(this).siblings('.ext-share').children().length;
      $(this).siblings('.ext-share').css($property, $width * $total);
      $(this).parent().addClass('expanded');
    }
  });
  /*share widget*/
  $(document).on("click", '.entry-share a', function (e) {
    e.preventDefault();
    jQuery.post($(this).parent('.entry-share').data('ajaxurl'), {social_pid: $(this).parent('.entry-share').data('id'), social_name: $(this).attr('class')});
    switch ($(this).attr('class')) {
      case'facebook':
        window.open('https://www.facebook.com/sharer/sharer.php?u=' + jQuery(this).attr('href'), "facebookWindow", "height=380,width=660,resizable=0,toolbar=0,menubar=0,status=0,location=0,scrollbars=0");
        break;
      case'twitter':
        window.open('http://twitter.com/intent/tweet?text=' + $(this).data('title') + ' ' + jQuery(this).attr('href'), "twitterWindow", "height=380,width=660,resizable=0,toolbar=0,menubar=0,status=0,location=0,scrollbars=0");
        break;
      case'pinterest':
        window.open('http://pinterest.com/pin/create/button/?url=' + jQuery(this).attr('href') + '&media=' + $(this).closest('.single-content').find('img').first().attr('src') + '&description=' + $(this).data('title'), "pinterestWindow", "height=640,width=660,resizable=0,toolbar=0,menubar=0,status=0,location=0,scrollbars=0");
        break;
      case'google':
        window.open('https://plus.google.com/share?url=' + jQuery(this).attr('href'), "googleWindow", "height=640,width=660,resizable=0,toolbar=0,menubar=0,status=0,location=0,scrollbars=0");
        break;
      case'linkedin':
        window.open('https://www.linkedin.com/shareArticle??mini=true&url=' + jQuery(this).attr('href') + '&title=' + $(this).data('title'), "linkedinWindow", "height=640,width=660,resizable=0,toolbar=0,menubar=0,status=0,location=0,scrollbars=0");
        break;
    }

    return false;
  });
});

$(document).ready(function() {
  $('img').each(function(){
    var $img = $(this);
    var filename = $img.attr('src')
    $img.attr('title', filename.substring((filename.lastIndexOf('/'))+1, filename.lastIndexOf('.')));

    $img.attr('alt', filename.substring((filename.lastIndexOf('/'))+1, filename.lastIndexOf('.')));
  });
});

$(document).ready(function(){function relatedPost(g,e,r){$.ajax({url:"/feeds/posts/default/-/"+e+"?alt=json-in-script&max-results="+r,type:"get",dataType:"jsonp",success:function(t){for(var u="",h='<div class="related">',x=0;x<t.feed.entry.length;x++){for(var z=0;z<t.feed.entry[x].link.length;z++){if("alternate"==t.feed.entry[x].link[z].rel){u=t.feed.entry[x].link[z].href;break}}
                                                                                                                                                                                                                                                            var p=t.feed.entry[x].title.$t;var c=t.feed.entry[x].content.$t;var y=$('<div>').html(c);if(c.indexOf("http://www.youtube.com/embed/")>-1||c.indexOf("https://www.youtube.com/embed/")>-1){var d=t.feed.entry[x].media$thumbnail.url,m=d.replace('/default.jpg','/mqdefault.jpg'),k=m;}else if(c.indexOf("<img")>-1){var s=y.find('img:first').attr('src'),v=s.replace('s72-c','s600');var k=v;}else{var k='http://1.bp.blogspot.com/-eAeO-DYJDws/Vkqtj4HFBFI/AAAAAAAAB0o/Q5OLsyONXM0/s1600-r/nth.png';}
                                                                                                                                                                                                                                                            h+='<li><div class="related-thumb"><a class="related-img" href="'+u+'" style="background:url('+k+') no-repeat center center;background-size: cover"/></div><h3 class="related-title"><a href="'+u+'">'+p+'</a></h3></li>'}
                                                                                                                                                                                      h+='</div>',g.html(h);}})}
                             $("#related-posts").each(function(){var g=$(this),e=g.text(),r=3;relatedPost(g,e,r);});});

